    COALESCE(jsonb_agg(jsonb_build_object('media_id', mf.id, 'file_url', mf.file_url, 'alt_text', mf.alt_text, 'media_type', cbm.media_type, 'display_order', cbm.display_order) ORDER BY cbm.display_order) FILTER (WHERE (mf.id IS NOT NULL)), '[]'::jsonb) AS media_files
   FROM ((public.content_blocks cb
     LEFT JOIN public.content_block_media cbm ON ((cb.id = cbm.content_block_id)))
     LEFT JOIN public.media_files mf ON ((cbm.media_file_id = mf.id)))
  GROUP BY cb.id, cb.section_id, cb.display_order;


