CREATE OR REPLACE VIEW public.v_doctors_with_specialties AS
 SELECT d.id,
    d.first_name,
    d.last_name,
    d.slug,
    d.designation,
    d.qualifications,
    d.experience_years,
    d.specializations,
    d.about,
    d.achievements,
    d.languages,
    d.consultation_fee,
    d.is_active,
    d.is_featured,
    d.display_order,
    COALESCE(string_agg((s.name)::text, ','::text ORDER BY ds.is_primary DESC, (s.name)::text) FILTER (WHERE (s.name IS NOT NULL)), ''::text) AS specialties,
    COALESCE(string_agg((s.slug)::text, ','::text ORDER BY ds.is_primary DESC, (s.slug)::text) FILTER (WHERE (s.slug IS NOT NULL)), ''::text) AS specialty_slugs
   FROM ((public.doctors d
     LEFT JOIN public.doctor_specialties ds ON ((d.id = ds.doctor_id)))
     LEFT JOIN public.specialties s ON ((ds.specialty_id = s.id)))
  WHERE (d.is_active = true)
  GROUP BY d.id, d.display_order, d.last_name, d.first_name
  ORDER BY d.display_order, d.last_name, d.first_name;


